package a.b.c.ch3;

// import


public class ExFlow_8_1
{
	// 상수 
	// 멤버변수
	// 생성자

	// 함수 

	// main() 함수 : 프로그램 시작점
	public static void main(String[] args) {
		// TODO Auto-generated method stub.

		for (int i=0; i < 3; i++ )
		{
			System.out.print((i < 3) + " : ");
			//System.out.println(i++);
			System.out.println("for i >>> : " + i);	
		}
		System.out.println("################");	
		for (int i=0; i <= 3; i++ )
		{
			System.out.print((i <= 3) + " : ");
			//System.out.println(i++);
			System.out.println("for i >>> : " + i);	
		}
	}
}
